
public class ConstanteOperadores {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		final double dolar = 0.058;
		final double salario = 6545.25;
		String nombre = "David";
		double salarioDolares = salario * dolar;
		System.out.println("**************************");
		System.out.println("Clase de Constamte sy Operadores");
		System.out.println("**************************");
		System.out.println("El salio en dolares de " + nombre + " es de: " + salarioDolares);
		System.out.println("Fin del la aplicacion");
		System.out.println("**************************");

	}

}
