
public class UsoBucleFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
	
		
		System.out.println("******************************");
		System.out.println("Bucle determinado FOR");		
		System.out.println("******************************");
		
		for(int i=0; i<10; i++) {
			System.out.println(i +" Juan");
		
		
		}
		
		System.out.println("******************************");
		System.out.println("******************************");
	}

}
