

public class Variable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.print("******************************************\n");
		System.out.print("----------------Declaracion de Variables!-----------\n");
		
		
		String nombre = "Ana";
		byte edad = 27;
		double salario = 1800.50;
		boolean carnet= true;
		
		System.out.print("Se trabajo con los tipos de datos primitivos y son: \n" + 
						"  * Tipo String: " + nombre + "\n" +
						"  * Tipo Byte: " + edad + "\n" +
						"  * Tipo Double: " + salario + "\n" +
						"  * Tipo Boolean: " + carnet + "\n" 
						);
		
		System.out.print("Termina la ejecucion del programa\n");
		System.out.print("******************************************\n");
	

	}

}
